"use strict";

var Aerospike = require('aerospike');

var shared = require('./shared');

shared.runner();

function add(client, argv) {
  var key, bins;
  return regeneratorRuntime.async(function add$(_context) {
    while (1) {
      switch (_context.prev = _context.next) {
        case 0:
          key = new Aerospike.Key(argv.namespace, argv.set, argv.key);
          bins = shared.cli.parseBins(argv.bins);
          _context.next = 4;
          return regeneratorRuntime.awrap(client.add(key, bins));

        case 4:
          console.info('Record updated successfully:', key);

        case 5:
        case "end":
          return _context.stop();
      }
    }
  });
}

exports.command = 'add <key> <bins...>';
exports.describe = 'Add one or more values to existing bin(s)';
exports.handler = shared.run(add);