"use strict";

var Aerospike = require('aerospike');

var shared = require('./shared');

shared.runner();

function get(client, argv) {
  var key, record;
  return regeneratorRuntime.async(function get$(_context) {
    while (1) {
      switch (_context.prev = _context.next) {
        case 0:
          key = new Aerospike.Key(argv.namespace, argv.set, argv.key);

          if (!argv.bins) {
            _context.next = 7;
            break;
          }

          _context.next = 4;
          return regeneratorRuntime.awrap(client.select(key, argv.bins));

        case 4:
          record = _context.sent;
          _context.next = 10;
          break;

        case 7:
          _context.next = 9;
          return regeneratorRuntime.awrap(client.get(key));

        case 9:
          record = _context.sent;

        case 10:
          console.info(record);

        case 11:
        case "end":
          return _context.stop();
      }
    }
  });
}

exports.command = 'get <key>';
exports.describe = 'Fetch a record from the database';
exports.handler = shared.run(get);
exports.builder = {
  bins: {
    desc: 'Select specific bins to fetch',
    type: 'array'
  }
};