"use strict";

var Aerospike = require('aerospike');

var shared = require('./shared');

var op = Aerospike.operations;
var lists = Aerospike.lists;
shared.runner();

function operate(client, argv) {
  var key, i, ops, results;
  return regeneratorRuntime.async(function operate$(_context) {
    while (1) {
      switch (_context.prev = _context.next) {
        case 0:
          key = new Aerospike.Key(argv.namespace, argv.set, argv.key);
          i = shared.random["int"](1, 10);
          ops = [lists.append('values', i), op.read('values'), op.incr('sum', i), op.read('sum')];
          _context.next = 5;
          return regeneratorRuntime.awrap(client.operate(key, ops));

        case 5:
          results = _context.sent;
          console.info(results);

        case 7:
        case "end":
          return _context.stop();
      }
    }
  });
}

exports.command = 'operate <key>';
exports.describe = 'Perform multiple operations on a single record';
exports.handler = shared.run(operate);