"use strict";

var Aerospike = require('aerospike');

var shared = require('./shared');

shared.runner();

function remove(client, argv) {
  var key;
  return regeneratorRuntime.async(function remove$(_context) {
    while (1) {
      switch (_context.prev = _context.next) {
        case 0:
          key = new Aerospike.Key(argv.namespace, argv.set, argv.key);
          _context.next = 3;
          return regeneratorRuntime.awrap(client.remove(key));

        case 3:
          console.info('Removed record:', key);

        case 4:
        case "end":
          return _context.stop();
      }
    }
  });
}

exports.command = 'remove <key>';
exports.describe = 'Remove a record from the database';
exports.handler = shared.run(remove);