'use strict';

function evaluateBinary(node) {
  var op = node.operator;
  var left = evaluateConst(node.left);
  var right = evaluateConst(node.right);
  if (op === '+') return left + right;
  throw new Error("Unsupported binary operation: \"".concat(op, "\""));
}

function evaluateConst(node) {
  if (node.type === 'Literal') return node.value;
  if (node.type === 'BinaryExpression') return evaluateBinary(node);
  throw new Error("Unsupported node type: \"".concat(node.type, "\""));
}

module.exports = evaluateConst;