"use strict";

var _require = require('../index'),
    dd = _require.dd;

var _require2 = require('../index'),
    dump = _require2.dump;

describe('dump helpers', function () {
  it('should be variadic', function () {
    var log = jest.spyOn(console, 'log').mockImplementation(function (any) {
      return any;
    });
    dump(1, 'a', {
      b: 2
    });
    expect(log).toHaveBeenCalled();
    expect(log.mock.calls.length).toBe(2);
  });
  it('should not exit the process when dump method called', function () {
    var exit = jest.spyOn(process, 'exit').mockImplementation(function (number) {
      return number;
    });
    dump(1);
    expect(exit).not.toHaveBeenCalled();
  });
  it('should kill the process when dd method called', function () {
    var exit = jest.spyOn(process, 'exit').mockImplementation(function (number) {
      return number;
    });
    dd(1);
    expect(exit).toHaveBeenCalled();
  });
});