"use strict";

require("core-js/modules/es.array.concat");

/* eslint-disable no-console */
var callerId = require('caller-id');

var Dumper = require('./dumper');

function dd(obj) {
  var dumper = new Dumper();
  var caller = callerId.getData(); // Print the file path, line number and generated dump

  console.log("".concat(caller.filePath, ":").concat(caller.lineNumber, ":"));
  console.log(dumper.generateDump(obj));
  process.exit(0);
}

module.exports = dd;