'use strict';

var test = require('tap').test;

var build = require('..');

test('Should clean the cache', function (t) {
  t.plan(1);
  var schema = {
    $id: 'test',
    type: 'string'
  };

  try {
    build(schema);
    build(schema);
    t.pass();
  } catch (err) {
    t.fail(err);
  }
});
test('Should clean the cache with external schemas', function (t) {
  t.plan(1);
  var schema = {
    $id: 'test',
    definitions: {
      def: {
        type: 'object',
        properties: {
          str: {
            type: 'string'
          }
        }
      }
    },
    type: 'object',
    properties: {
      obj: {
        $ref: '#/definitions/def'
      }
    }
  };

  try {
    build(schema);
    build(schema);
    t.pass();
  } catch (err) {
    t.fail(err);
  }
});