'use strict';

var test = require('tap').test;

var build = require('..');

test('object with custom format field', function (t) {
  t.plan(1);
  var schema = {
    title: 'object with custom format field',
    type: 'object',
    properties: {
      str: {
        type: 'string',
        format: 'test-format'
      }
    }
  };
  var stringify = build(schema);

  try {
    stringify({
      str: 'string'
    });
    t.pass();
  } catch (e) {
    t.fail();
  }
});