'use strict';

module.exports = state;

function state(o) {
  var secret = o.secret,
      censor = o.censor,
      isCensorFct = o.isCensorFct,
      compileRestore = o.compileRestore,
      serialize = o.serialize,
      groupRedact = o.groupRedact,
      nestedRedact = o.nestedRedact,
      wildcards = o.wildcards,
      wcLen = o.wcLen;
  var builder = [{
    secret,
    censor,
    isCensorFct,
    compileRestore
  }];
  builder.push({
    secret
  });
  if (serialize !== false) builder.push({
    serialize
  });
  if (wcLen > 0) builder.push({
    groupRedact,
    nestedRedact,
    wildcards,
    wcLen
  });
  return Object.assign(...builder);
}