'use strict';

require("core-js/modules/es.array.join");

require("core-js/modules/es.array.join");

var fs = require('fs');

var path = require('path');

var fastify = require('../fastify')({
  https: {
    key: fs.readFileSync(path.join(__dirname, '../test/https/fastify.key')),
    cert: fs.readFileSync(path.join(__dirname, '../test/https/fastify.cert'))
  }
});

var opts = {
  schema: {
    response: {
      '2xx': {
        type: 'object',
        properties: {
          hello: {
            type: 'string'
          }
        }
      }
    }
  }
};
fastify.get('/', opts, function (req, reply) {
  reply.header('Content-Type', 'application/json').code(200);
  reply.send({
    hello: 'world'
  });
});
fastify.listen(3000, function (err) {
  if (err) throw err;
  console.log("server listening on ".concat(fastify.server.address().port));
});