'use strict';

var t = require('tap');

var test = t.test;

var fastify = require('..')();

var noop = function noop() {};

var opts = {
  schema: {
    response: {
      '2xx': {
        type: 'object',
        properties: {
          hello: {
            type: 'string'
          }
        }
      }
    }
  }
};
test('chainable - get', function (t) {
  t.plan(1);
  t.type(fastify.get('/', opts, noop), fastify);
});
test('chainable - post', function (t) {
  t.plan(1);
  t.type(fastify.post('/', opts, noop), fastify);
});
test('chainable - route', function (t) {
  t.plan(1);
  t.type(fastify.route({
    method: 'GET',
    url: '/other',
    schema: opts.schema,
    handler: noop
  }), fastify);
});