'use strict';

var test = require('tap').test;

var _require = require('../../lib/schemas'),
    Schemas = _require.Schemas;

test('Should not change resolved schema', function (t) {
  t.plan(4);
  var schemas = new Schemas();
  schemas.add({
    $id: 'A',
    field: 'value'
  });
  var schema = {
    a: 'A#'
  };
  var resolvedSchema = schemas.resolveRefs(schema);
  t.same(resolvedSchema.a, {
    field: 'value'
  });
  t.same(resolvedSchema.$id, undefined);
  schemas.getJsonSchemas();
  t.same(resolvedSchema.a, {
    field: 'value'
  });
  t.same(resolvedSchema.$id, undefined);
});