'use strict';

var t = require('tap');

var test = t.test;

var Fastify = require('..');

test('nullable string', function (t) {
  t.plan(3);
  var fastify = Fastify();
  fastify.route({
    method: 'POST',
    url: '/',
    handler: function handler(req, reply) {
      t.same(req.body.hello, null);
      reply.code(200).send(req.body);
    },
    schema: {
      body: {
        type: 'object',
        properties: {
          hello: {
            type: 'string',
            format: 'email',
            nullable: true
          }
        }
      },
      response: {
        200: {
          type: 'object',
          properties: {
            hello: {
              type: 'string',
              format: 'email',
              nullable: true
            }
          }
        }
      }
    }
  });
  fastify.inject({
    method: 'POST',
    url: '/',
    body: {
      hello: null
    }
  }, function (err, res) {
    t.error(err);
    t.same(res.payload.hello, null);
  });
});