'use strict';

var test = require('tap').test;

var flatstr = require('.');

test('does not throw', function (t) {
  t.doesNotThrow(function () {
    flatstr('abc');
  });
  t.doesNotThrow(function () {
    flatstr({});
  });
  t.doesNotThrow(function () {
    flatstr(1);
  });
  t.doesNotThrow(function () {
    flatstr(null);
  });
  t.end();
});
test('returns the same value that was passed in', function (t) {
  var o = {};
  t.is(flatstr('abc'), 'abc');
  t.is(flatstr(o), o);
  t.end();
});