'use strict';

var _require = require('url'),
    URL = _require.URL;

var BASE_URL = 'http://localhost';
/**
 * Parse URL
 *
 * @param {(Object|String)} url
 * @param {Object} [query]
 * @return {URL}
 */

module.exports = function parseURL(url, query) {
  var result = typeof url === 'object' ? Object.assign(new URL(BASE_URL), url) : new URL(url, BASE_URL);
  var merged = Object.assign({}, url.query, query);
  Object.keys(merged).forEach(function (key) {
    var value = merged[key];

    if (Array.isArray(value)) {
      result.searchParams.delete(key);
      value.forEach(function (param) {
        result.searchParams.append(key, param);
      });
    } else {
      result.searchParams.set(key, value);
    }
  });
  return result;
};