'use strict';

var SonicBoom = require('..');

var out = new SonicBoom(process.stdout.fd);
var str = Buffer.alloc(1000).fill('a').toString();
var i = 0;

function write() {
  if (i++ === 10) {
    return;
  }

  if (out.write(str)) {
    write();
  } else {
    out.once('drain', write);
  }
}

write();