"use strict";

var _require = require('../../src'),
    Rule = _require.Rule;

test('boolean returns true if true tested', function () {
  var rule = new Rule({
    type: 'boolean'
  }, null);
  expect(rule.test(true)).toBe(true);
});
test('boolean returns true if false tested', function () {
  var rule = new Rule({
    type: 'boolean',
    toBe: false
  }, null);
  expect(rule.test(false)).toBe(true);
});