"use strict";

var _require = require('../../src'),
    Rule = _require.Rule;

test('returns true if function', function () {
  var functionRule = new Rule({
    type: 'function'
  }, null);
  expect(functionRule.test(function () {})).toBe(true);
});
test('returns true if function result matches a Rule', function () {
  function someFunctionThatReturnsAnInt(_int) {
    return _int * 5;
  }

  var functionTest = new Rule({
    type: 'function',
    result: {
      of: 5,
      toBe: new Rule('int')
    }
  }, null);
  expect(functionTest.test(someFunctionThatReturnsAnInt)).toBe(true);
});
test('returns false if function result doesn\'t match a Rule', function () {
  var functionRule = new Rule({
    type: 'function',
    result: {
      of: 5,
      toBe: new Rule('int')
    }
  }, null);
  expect(functionRule.test(function () {
    return 'val - 10';
  })).toBe(false);
});