"use strict";

require("core-js/modules/es.array.concat.js");

require("core-js/modules/es.array.concat.js");

var _require = require('../../src'),
    Rule = _require.Rule;

test('type rule.getError() returns array of errors when input is wrong', function () {
  var strRule = new Rule({
    type: 'string'
  }, 'strRule must be string');
  expect(strRule.getError(1)).toBe('strRule must be string');
});
test('type rule.getError() call error function (if the error of the rule is function)', function () {
  var strRule = new Rule({
    type: 'string'
  }, function (key, value) {
    return "".concat(key, " must be a string, ").concat(value, " is not a string");
  });
  expect(strRule.getError('something', 1)).toBe('something must be a string, 1 is not a string');
});