"use strict";

var _require = require('../../src'),
    Rule = _require.Rule;

test('non valid string boolean', function () {
  var stringBoolean = new Rule({
    type: 'string-boolean'
  }, null);
  expect(stringBoolean.test('hello')).toBe(false);
});
test('valid string boolean', function () {
  var stringBoolean = new Rule({
    type: 'string-boolean'
  }, null);
  expect(stringBoolean.test('true')).toBe(true);
  expect(stringBoolean.test('false')).toBe(true);
});
test('string boolean is true', function () {
  var stringBoolean = new Rule({
    type: 'string-boolean',
    toBe: true
  }, null);
  expect(stringBoolean.test('true')).toBe(true);
  expect(stringBoolean.test('false')).toBe(false);
});