"use strict";

var _require = require('../../src'),
    Rule = _require.Rule;

test('type email false', function () {
  var urlRule = new Rule({
    type: 'url'
  }, null);
  expect(urlRule.test([])).toBe(false);
});
test('type url false', function () {
  var urlRule = new Rule({
    type: 'url'
  }, null);
  expect(urlRule.test('frfrfr')).toBe(false);
});
test('type url true', function () {
  var urlRule = new Rule({
    type: 'url'
  }, null);
  expect(urlRule.test('https://google.fr')).toBe(true);
});
test('type domain true', function () {
  var urlRule = new Rule({
    type: 'url',
    domain: function domain(_domain) {
      return _domain === 'google.fr';
    }
  }, null);
  expect(urlRule.test('https://google.fr')).toBe(true);
});
test('type protocol true', function () {
  var urlRule = new Rule({
    type: 'url',
    protocol: function protocol(prot) {
      return prot === 'https';
    }
  }, null);
  expect(urlRule.test('https://google.fr')).toBe(true);
});