"use strict";

var _require = require('../../src/util'),
    looseEqual = _require.looseEqual,
    flattenObject = _require.flattenObject;

test('looseEqual object false', function () {
  var a = {
    a: 'hello',
    b: 'cccc'
  };
  var b = {
    a: 'cd',
    b: 'cc'
  };
  expect(looseEqual(a, b)).toBe(false);
});
test('looseEqual object true', function () {
  var a = {
    a: 'hello',
    b: 'cccc'
  };
  var b = {
    a: 'hello',
    b: 'cccc'
  };
  expect(looseEqual(a, b)).toBe(true);
});
test('looseEqual object and array fals', function () {
  var a = {
    a: 'hello',
    b: 'cccc'
  };
  var b = ['hello', 'cccc'];
  expect(looseEqual(a, b)).toBe(false);
});
test('flatten Object', function () {
  var a = {
    a: 'hello',
    b: {
      d: 'dazda'
    }
  };
  expect(flattenObject(a)).toEqual({
    a: 'hello',
    'b.d': 'dazda'
  });
});