"use strict";

var _require = require('../../src/Validator/util'),
    traverse = _require.traverse,
    getValFromPath = _require.getValFromPath;

test('test getValFromPath returns 5 path is correct', function () {
  var obj = {
    a: {
      b: 5
    }
  };
  expect(getValFromPath('a.b', obj)).toBe(5);
});
test('test travers', function (done) {
  var obj = {
    a: {
      b: 5
    }
  };
  traverse(obj, function (val, path) {
    expect(val).toBe(5);
    expect(path).toBe('a.b');
    done();
  });
});