"use strict";

var Twit = require('twit'); // eslint-disable-line


var T = new Twit({
  consumer_key: process.env.consumer_key,
  consumer_secret: process.env.consumer_secret,
  access_token: process.env.access_token,
  access_token_secret: process.env.access_token_secret,
  timeout_ms: 60 * 1000,
  strictSSL: true
});

var json = require('../package.json');

var message = "Hey there! The new release ".concat(json.version, " of ForgJs is available on #github and #npm.\nHave fun and code with love \u2764\nP.S. Im a robot running on @circleci");
T.post('statuses/update', {
  status: message
}, function (err) {
  if (!err) {
    console.log('Grate job'); // eslint-disable-line
  }
});