"use strict"; // Import.

require("core-js/modules/es.array.concat");

var CColor = require('ccolor');

var jcopy = require('./app'); // Constants.


var TEST_PASSED_MESSAGE = 'PASSED';
var TEST_FAILED_MESSAGE = 'FAILED';
var TEST_FAILED_WITH_EXCEPTION = 'FAILED WITH EXCEPTION';
var OBJECT = {
  a: {
    b: {
      c: 100
    }
  }
};
var TESTS_RESULTS = [100, true, false]; // Define tests.

var tests = [function () {
  return jcopy(OBJECT).a.b.c;
}, function () {
  return OBJECT.a.b.c === jcopy(OBJECT).a.b.c;
}, function () {
  return OBJECT === jcopy(OBJECT);
}]; // Run tests.

console.log(CColor.yellow('Tests started...'));

for (var i = 0; i < tests.length; i++) {
  try {
    var isTestPassed = tests[i]() === TESTS_RESULTS[i];
    console.log("Test ".concat(i + 1, ": ").concat(isTestPassed ? CColor.green(TEST_PASSED_MESSAGE) : CColor.red(TEST_FAILED_MESSAGE), "."));
  } catch (err) {
    console.log("Test ".concat(i + 1, ": ").concat(CColor.red(TEST_FAILED_WITH_EXCEPTION), "."));
  }
}

console.log(CColor.yellow('Tests done.'));