'use strict';

require("core-js/modules/es.array.for-each");

require("core-js/modules/es.array.for-each");

var expect = require('chai').expect;

var utils = require('../../lib/utils');

describe('utils', function () {
  describe('isValidDatabaseName', function () {
    it('should be valid', function () {
      var validNames = ['somedb_123123', 'somedb-123123', 'SOME_DB-1231', 'SOMEDB&1231'];
      validNames.forEach(function (n) {
        expect(utils.isValidDatabaseName(n)).to.equal(true, "Expected \"".concat(n, "\" to be a valid name"));
      });
    });
    it('should be invalid', function () {
      var invalidNames = ['', 'somedb 123123', 'SOME$DB1231', 'SOMEDB<1231', 'SOMEDB>1231', '1234567890123456789012345678901234567890123456789012345678901234', 'SOMEDB"123'];
      invalidNames.forEach(function (n) {
        expect(utils.isValidDatabaseName(n)).to.equal(false, "Expected \"".concat(n, "\" to be an invalid name"));
      });
    });
  });
});