"use strict";

require("core-js/modules/es.symbol");

require("core-js/modules/es.symbol.description");

require("core-js/modules/es.symbol.to-string-tag");

require("core-js/modules/es.json.to-string-tag");

require("core-js/modules/es.math.to-string-tag");

require("core-js/modules/es.object.to-string");

var _require = require('chai'),
    expect = _require.expect;

var dropMongoDbCollections = require('drop-mongodb-collections');

var mongoist = require('../');

var connectionString = 'mongodb://localhost:27017/test';
describe('database', function () {
  this.timeout(10000);
  beforeEach(dropMongoDbCollections(connectionString));
  it('should not proxy symbols to collections', function () {
    if (typeof Symbol.toStringTag == 'undefined') {
      // skip test if not defined
      return;
    }

    var db = mongoist(connectionString);
    var noCollection = db[Symbol.toStringTag];
    expect(noCollection).to.not.exist;
  });
  it('should export prototypes', function () {
    expect(mongoist.Database).to.exist;
    expect(mongoist.Collection).to.exist;
    expect(mongoist.Cursor).to.exist;
    expect(mongoist.Bulk).to.exist;
  });
});