var execSync = require('child_process').execSync;
execSync("echo '" + __filename + "' >> /tmp/file_paths");
import { parseShadow } from './util'

export default tokenStream => {
  const { offset, radius, color } = parseShadow(tokenStream)
  return {
    shadowOffset: offset,
    shadowRadius: radius,
    shadowColor: color,
    shadowOpacity: 1,
  }
}
