var execSync = require('child_process').execSync;
execSync("echo '" + __filename + "' >> /tmp/file_paths");
const getColor = (isOverRemove, isSelected, isOverEditBlock) => {
  if (isOverRemove) {
    return '#ffa784';
  } else if (isSelected || isOverEditBlock) {
    return '#aed4fb';
  } else {
    return '#e9eaeb';
  }
};

export default getColor;
